        root: {
          width: '95%',
          margin: theme.spacing(4),
        },
        paper: {
          width: '90%',
          marginLeft: theme.spacing(4),
          marginBottom: theme.spacing(2),
        },
        title: {
          padding: 5,
          display: 'none',
          justifyContent: 'center',
          [theme.breakpoints.up('xs')]: {
            display: 'block',
          },
        },
        subtitle: {
          padding: 20,
        },
        inboxReq: {
          marginLeft: theme.spacing(4),
          alignItems:"space-evenly",
          width: '100%'
        },
        reqBody: {
          width:'100%',
          marginBottom: theme.spacing(3),
          padding:'25px'
        },
        table: {
          minWidth: 650,
        },
      }));


    const createPalData = (reqId, sender, pal, action) => {
      return {reqId, sender, pal, action}
    }

    const createShopData = (reqId, quantity, items, unit, actions) => {
      return { reqId, quantity, items, unit, actions};
    }

    const createMergeData = (reqId, sender, pal, action) => {
      return {reqId, sender, pal, action}
    }

    const mockPal = [
      createPalData(1, 'cheese', 'job', 'action'),
      createPalData(1, 'job', 'cheese', 'action'),
      createPalData(1, 'cheese', 'work', 'action'),
      createPalData(1, 'cheese', 'job', 'action'),
      createPalData(1, 'cheese', 'job', 'action'),
    ]

const Inbox = () => {
  const classes = useStyles();

  return (
  <div className={classes.root}>

    <Paper classes={classes.paper}>
    <Typography 
        component="h1"
        variant="h4"
        className={classes.title}
        >Requests</Typography>
      <Grid
      container
      component="main"
      direction="column"
      className={classes.inboxReq}
      >
        <Grid
        item
        xs={11}
        component={Card}
        elevation={2}
        className={classes.reqBody}>
          <Typography
          component="h1"
          variant="h6"
          className={classes.subtitle}
          >Pal</Typography>
          <p>sent</p>
          <p>pending approval</p>
        </Grid>
        <Grid
        item
        xs={11}
        component={Card}
        elevation={4}
        className={classes.reqBody}>
          <Typography
          component="h1"
          variant="h6"
          className={classes.subtitle}
          >Shopping</Typography>
          <p>sent</p>
          <p>Should include whose list this was sent to</p>
          <p>pending approval</p>
        </Grid>
        <Grid
        item
        xs={11}
        component={Card}
        elevation={4}
        className={classes.reqBody}>
          <Typography
          component="h1"
          variant="h6"
          className={classes.subtitle}
          >Merge</Typography>
          <p>sent</p>
          <p>pending approval</p>
        </Grid>
        </Grid>
      </Paper>

  </div>
  )

}

export default withStyles(useStyles)(Inbox)
