import React, {Component} from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import { withStyles } from '@material-ui/styles';

//the main page, go not pass go, to not collect $200 without login in or creating a login
const useStyles = makeStyles((theme, props) => ({
  root: props => ({
    height: '100vh',
  }),
  image: props =>({
    backgroundImage: 'url(https://unsplash.com/photos/FDQFZHY9iG4)',
    backgroundRepeat: 'no-repeat',
    // backgroundColor:
    //   theme.palette.type === 'light' ? theme.palette.grey[50] : theme.palette.grey[900],
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  }),
  paper: props =>({
    margin: theme.spacing(8, 4),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  }),
  avatar: props => ({
    margin: theme.spacing(1),
    // backgroundColor: theme.palette.secondary.main,
  }),
  form: props => ({
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  }),
  submit: props => ({
    margin: theme.spacing(3, 0, 2),
  }),
}));

class LogIn extends Component {
  state = {
    username: '',
    password: '',
    loggedIn: false, 
  }


  handleText = e => {
    const state = {...this.state}
    state[e.target.name] = e.target.value
    this.setState(state)
    console.log(state)
  }

  loggedIn = e => {
    e.preventDefault()
    document.cookie = "businessCookies="+JSON.stringify({
      "username":this.state.username,
      "loggedIn":true,
    })
    document.cookie="max-Age:60*10000"
    window.location.replace('/listings')
  }

  render() {
    const {classes} = this.props
    return(

      <Grid container component="main" className={classes.root}>
      <CssBaseline />
      <Grid item xs={false} sm={4} md={7} className={classes.image} />
      <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
        <div className={classes.paper}>
          <Avatar className={classes.avatar}>
          </Avatar>
          <Typography component="h1" variant="h5">
            Sign in
          </Typography>
          <form className={classes.form} noValidate>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
            />
            <FormControlLabel
              control={<Checkbox value="remember" color="primary" />}
              label="Remember me"
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
            >
              Sign In
            </Button>
            <Grid container>
              <Grid item xs>
                <Link href="#" variant="body2">
                  Forgot password?
                </Link>
              </Grid>
              <Grid item>
                <Link href="#" variant="body2">
                  {"Don't have an account? Sign Up"}
                </Link>
              </Grid>
            </Grid>
            <Box mt={5}>

            </Box>
          </form>
        </div>
      </Grid>
    </Grid>


    )
  }


}

export default withStyles(useStyles)(LogIn)
