import {LOGIN, USER_INFORMATION} from '../actions/types'
import state from '../state'

let initialState = {...state}

console.log('find the state login ', initialState)

export default function(initialState, action){
    switch(action.type){
        case LOGIN:
        return [...initialState.user, action.payload]
        case USER_INFORMATION:
        return  [...initialState.user, action.payload.userInfo]
        default: return initialState
    }

}
