import {connect} from 'react-redux'
import ContentShifter from '../Components/ContentShifter'
import {drawerState} from '../redux/actions/drawerState'

const mapStateToProps = (state) => {
  return {
    openDrawer: state.openDrawer
  }
}

const mapStateToDispatch = (dispatch) => {

  return {
    drawerState: (boo) => dispatch(drawerState(boo))
  }
}


export default connect(mapStateToProps, mapStateToDispatch)(ContentShifter)
