import React, {useState, Fragment} from 'react';
import clsx from 'clsx';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import { withStyles } from '@material-ui/styles';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import Dialog from '@material-ui/core/Dialog';
import ListItemText from '@material-ui/core/ListItemText';
import ListItem from '@material-ui/core/ListItem';
import List from '@material-ui/core/List';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { library, dom } from '@fortawesome/fontawesome-svg-core';
import { faPlusCircle} from '@fortawesome/free-solid-svg-icons';
import Slide from '@material-ui/core/Slide';
import FoodSearchBar from '../Containers/FoodSearchBar';
import CloseIcon from '@material-ui/icons/Close';
import './toolbar.css'

library.add(faPlusCircle) 
dom.watch()

const useStyles = makeStyles((theme) => ({
  appBar: {
    position: 'relative',
  },
  title: {
    marginLeft: theme.spacing(2),
    flex: 1,
  },
  resultCard: {
    maxWidth: 345,
    marginTop: theme.spacing(4),
    marginLeft: theme.spacing(1),
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  actionList: {
    width: '100vw'
  },
  actions: {
    display: 'flex',
    justifyContent:"space-around",

  },
  fas: {
    display: 'flex',
    justifyContent:"space-between",
  }
}));

const SearchPage = (props) => {
  console.log('search page props',props)
  const classes = useStyles();
  const {searchResults, openFoodSearch, openFoodFinder} = props

  const [expanded, setExpanded] = useState(true)

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  return (
    <div>

    <Tooltip className="add" title="Add Item to your shopping list">
      <IconButton 
      onClick={() => openFoodSearch(true)}>
        <svg className="fas fa-plus-circle"></svg>
      </IconButton>
    </Tooltip> 


    <Dialog open={openFoodFinder} fullScreen >
    <AppBar className={classes.appBar}>
      <Toolbar>
      <IconButton edge="start" color="inherit" onClick={() => openFoodSearch(false)} 

      >
       <CloseIcon />
     </IconButton>
      </Toolbar>
    </AppBar>
    <FoodSearchBar/>
    <List>

      {searchResults.length <= 1  ? 
      <Grid
        container
        component="main" 
        direction="row"
        justify="center"
        alignItems="center"
        spacing={2}>
          {
           searchResults.map((item, id) => (
          <Grid item xs={10} sm={10} md={9} elevation={10}>
            <Card className={classes.root}>
            <CardMedia
              className={classes.media}
              image="/../../public/pantryImg.jpg"
              title="Paella dish"
            />
            <CardContent>
            <Typography
            variant="body2" color="textSecondary" component="p"
            >Food Title</Typography>
            </CardContent>
            <CardActions disableSpacing>
              <IconButton
                className={clsx(classes.expand, {
                  [classes.expandOpen]: expanded,
                })}
                onClick={handleExpandClick}
                aria-expanded={expanded}
                aria-label="show more"
              >
                <ExpandMoreIcon />
              </IconButton>
            </CardActions>
            <Collapse in={expanded} timeout="auto" unmountOnExit>
              <CardContent 
              className="actionList">
                <Button
                fullWidth="true"
                className="actions"
                ><svg className="fas fa-cart-arrow-down"/> 
                Add To Shopping List 
                <svg className="fas fa-cart-arrow-down"/></Button>

              </CardContent>
            </Collapse>
           </Card>
        </Grid>
           ))
          }
      </Grid> : 
      <div/>}

    </List>
  </Dialog>
    </div>
  )
}
//need to add an option to add manually at the bottom 
export default SearchPage
