import {USER_DETAILS,} from '../actions/types'
import state from '../state'

let initialState = {...state}

console.log('find the state login ', initialState)

export default function(initialState, action){
    console.log('nav bar action payload',action.payload)
    switch(action.type){
        case USER_DETAILS:
        let newState = {...initialState.user}
        return newState = action.payload.userDetails;
        // case LOGOUT: 
        // return state
        default: return initialState
    }

}
