import {LOGIN} from './types'
import axios from 'axios'

export const login = (user, password) => async dispatch => {
  console.log(`/preLogin/${user}`)
    try{
      // axios.default.header.get['Content-Type'] = ''
        const res = await axios({
          method: 'GET',
          header: {'content-type': 'application/json'}, 
          url: `http://localhost:4001/preLogin/${user}`,
          body: {
            user: `${user}`,
            password: `${password}`
          }
        })
        .then(res.params)
        .then(
        dispatch( {
            type: LOGIN,
            payload: res.json()
        })
      )
    }
    catch(e){
        res.send('sorry there was an error ')
    }

}
