import {LOGIN} from './types'
import axios from 'axios'

export const login = (username, password) => dispatch => {
  console.log('tell me the username', username.value, " and your password? ", password.value)
  // const url = `

    try{
      // axios.default.header.get['Content-Type'] = ''
        axios.request({
          baseline: `http://34.69.52.60:4001/`,
          url: `/preLogin/${username.value}`,
          method: 'GET',
          transformRequest:[{
            header: {
              'content-type': 'application/json'
            },
            body: {
              username: `${username.value}`,
              password: `${password.value}`
            }
          }]
        }
        )
        .then(res => res.json())
        .then(
        dispatch( {
            type: LOGIN,
            payload: res.json()
        })
      )
    }
    catch(e){
        console.log(`can't find that user ${username.value}`)
    }

}
