import {USER_INFORMATION} from '../'

const startState = {
  userInfo: {
    username: '',
    email: 'email address', 
    firstName:'first name', 
    lastName:'last name', 
    userLocationId:1 ,
    displayPrefId:1, 
    pantryId:1, 
    shopListId:1, 
    palListId:1, 
    accessId:1
  }
}

export default function(state = startState, action){
  console.log(action.payload)
  switch(action.type){
      case USER_INFORMATION:
      let newState = action.payload.userInfo
      return newState
      default: return state
  }

}
