import {USER_DETAILS, LOGOUT} from '../actions/types'
import state from '../state'

let initialState = {...state}

console.log('find the state login ', initialState)

export default function(newState = {...initialState.userDetails}, action){
    console.log('nav bar action payload',action.payload)
    switch(action.type){
        case USER_DETAILS:
        return newState = action.payload.userDetails;
        // case LOGOUT: 
        // return state
        default: return newState
    }

}
