import {USER_INFORMATION, LOGOUT} from '../actions/types'
import state from '../state'

let startingState = {...state}

console.log('this is the state for the nav bar - user', startingState.user)

export default function(state ={...startingState}, action) {
  console.log('action payload on nav bar', action.payload)
  switch(action.type) {
    case USER_INFORMATION: 
    let newState = {...state}
    return newState = action.payload
    case LOGOUT:
    let reset = {...state}
    return reset
    default: return state
  }
}
