import {LOGIN, USER_INFORMATION} from '../actions/types'
import state from '../state'

let initialState = []

console.log('find the state login ', startingState)

export default function(startingState = {...initialState}, action){
    switch(action.type){
        case LOGIN:
        return startingState = action.payload.user
        case USER_INFORMATION:
        return  startingState = action.payload.userInfo
        default: return state
    }

}
