            "username":user.username,
            "validation": true,
            "token": user.token,
            //will have to add validate for username and password, then can be true 
            "max-Age":60*10000,
            "reset": {
              "resetUsername": '',
              "resetLoggedIn": ''
            }

              })
              window.location.replace('/home')
        } else {
          console.log('sorry, no cookie')
        }

    }

  render() {
    const {classes} = this.props
    console.log(this.props)
    // user.validation ? <Home username={user.user} token={user.token} /> :
    return (
      <Grid 
      container 
      component="main" 
      className={classes.root}>
      <CssBaseline />
      <Grid item xs={false} sm={4} md={7} className={classes.image} />
      <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} >
        <div className={classes.paper}>
          <Avatar className={classes.avatar}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" 
          variant="h5"
          aria-label='sign in for pantry pals'
          >
            Sign in
          </Typography>

          <form className={classes.form}>
            <TextField
              onChange={this.handleText}
              value={this.state.username}
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="username"
              label="Username"
              name="username"
              type="text"
              autoFocus
              aria-label='your username'
            />
            <TextField
              onChange={this.handleText}
              value={this.state.password}
              variant="outlined"
              margin="normal"
              required
              fullWidth
              label="Password"
              type="password"
              id="password"
              aria-label='your password'
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
              aria-label='sign in button'
              onClick={() => {
                e.preventDefault();
                return props.login(username={...this.state.username},password={...this.state.password})
              }}
            >
              Sign In
            </Button>
            <Grid container>
              <Grid item xs>
                <ListItem
                button
                component={Link}
                to="/forgotPassword" 
                primary={'Forgot password'}
                aria-label='forgot password?'
                >Forgot Password</ListItem>
              </Grid>
              <Grid item>
                <ListItem
                button
                component={Link}
                to="/createNewUser" 
                aria-label='create a login for pantry pals'
                >
                  {"Don't have an account? Sign Up"}
                </ListItem>
              </Grid>
            </Grid>
            <Box mt={5}>
            </Box>
          </form>
        </div>
      </Grid>
    </Grid>

  )
}
}

