import {LOGIN} from '../actions/types'

const startState = {
    user:[],
    validation:false
}

export default function(state = startState, action){

    switch(action.type){
        case LOGIN:
        state =[]
        return {
            ...state,
            user:action.payload,
            validation: true

        }
        default: return state
    }

}
