import {LOGIN} from '../actions/types'
import store from '../'


// console.log('find the state ? ', startingState)

export default function(state = [...state.user], action){
    console.log(action.payload)
    switch(action.type){
        case LOGIN:
        let newState = [...state, action.payload.user]
        return newState
        default: return state
    }

}
