import {LOGIN} from '../actions/types'

const startState = {
    user: {
        username:'',
        password: ''    
    }  
}

export default function(state = startState, action){

    switch(action.type){
        case LOGIN:
        return {
            ...state,
            user:action.payload,
            validation: true

        }
        default: return state
    }

}
