import {USER_DETAILS, LOGOUT} from '../actions/types'
import state from '../state'

let initialState = {...state}

let reset = {
    user: {
      username:'',
      password: '',
      validation:false,
      pass: '',
      time:''
    },  
    userDetails: { 
      userLocationId:'' ,
      displayPrefId:'', 
      pantryId:'', 
      shopListId:'', 
      palListId:'', 
      accessId:'',
      run: false
    },
    newUser: false,
    userShopList: {
      call: false,
      list: []
    },
    openFoodFinder: false,
    searchResults: []

  }

export default function(state = {...initialState.userDetails}, action){
    switch(action.type){
        case USER_DETAILS:
        return action.payload.userDetails
        // case LOGOUT:
        // return reset
        default: return state
    }
}
