import {LOGIN} from '../actions/types'


console.log('find the state ? ', startingState)

export default function(state = [], action){
    console.log(action.payload)
    switch(action.type){
        case LOGIN:
        let newState = [{...state.user}, action.payload.user]
        return newState
        default: return state
    }

}
