import {LOGIN} from './types'
import axios from 'axios'

export const login = (username, password) => dispatch => {
  console.log('tell me the username', username.value, " and your password? ", password.value)
    try{
      // axios.default.header.get['Content-Type'] = ''
        axios.get({
          header: {'content-type': 'application/json'}, 
          url: `http://34.69.52.60:4001/preLogin/${username}`,
          body: {
            user: `${username}`,
            password: `${password}`
          }
        })
        .then(res.json())
        .then(
        dispatch( {
            type: LOGIN,
            payload: res.json()
        })
      )
    }
    catch(e){
        console.log(`can't find that user ${username}`)
    }

}
