import React from 'react'
import Paper from '@material-ui/core/Paper'
import Box from '@material-ui/core/Box'
import Typography from '@material-ui/core/Typography'
import TextField from '@material-ui/core/TextField'
import { makeStyles } from '@material-ui/core/style'
import Button from '@material-ui/core/Button'
import {useInput} from '../Hooks/inputHook'

const useStyles = makeStyles((theme) => ({
  root: {
    margin: '5%',
    paddingLeft: '5%',
    width: '90%',
  },
  paper: {
    width: '100%',
    justifyContent: 'space-evenly',
  },
  title: {
    padding: 10,
    display: 'none',
    [theme.breakpoints.up('sm')]: {
      display: 'block',
    },
  },
  fields: {
    margin: theme.spacing(2.5),
    width: '90%',
    },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(2),
  },
}));

const ManualAdd = () => {

  const {value: quantity, bind: bindQuantity, reset: resetQuantity} = useInput('') 
  const {value: item, bind: bindItem, reset: resetItem} = useInput('') 
  const {value: measurement, bind: bindMeasurement, reset: resetMeasurement} = useInput('') 

  return (
  <Paper>
    <Box>
      <Typography>Can't find what you are looking for?</Typography>
      <p>Manually Add Your Item here</p>
      <TextField
            {...bindQuantity}
            variant="outlined"
            margin="normal"
            required
            id="quantity"
            label="Quantity"
            name="quantity"
            type="number"
            autoFocus
            // className={classes.fields}
            aria-label="quantity of item you need"
          />
                <TextField
            {...bindItem}
            variant="outlined"
            margin="normal"
            required
            id="item"
            label="Item"
            name="item"
            type="text"
            autoFocus
            // className={classes.fields}
            aria-label="quantity of item you need"
          />
            {/* <TextField
            {...bindMeasurement}
            variant="outlined"
            margin="normal"
            required
            id="measurement"
            label="Measurement"
            name="measurement"
            type="text"
            autoFocus
            // className={classes.fields}
            aria-label="quantity of item you need"
          /> */}
          <Button>Save to Shopping List</Button>
    </Box>
  </Paper>
  )
}

export default ManualAdd
