import {USER_INFORMATION} from '../actions/types'

const startState = {

}

export default function(state = startState, action){
  console.log(action.payload)
  switch(action.type){
      case USER_INFORMATION:
      let newState = action.payload.userInfo
      return newState
      default: return state
  }

}
