import React, {useState} from 'react';
import {useHistory} from 'react-router-dom'
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';

const Congrats = (props) => {
  //should add something here to reset the state of the newUser state
  const history = useHistory();


  const handleRedirect = () => {
    history.push('/home')
  };

  return (
    <Paper>
      <Typography>

      </Typography>
       <Link to={{
         pathname:'/'
       }}>Log in Here</Link>



      <Dialog

        onClose={handleRedirect}
        aria-labelledby="alert, log in created"
        aria-describedby="congratulation on becoming a pantry pal user"
      >
        <DialogTitle id="alert-dialog-title">{"      Congratulations!"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
          You are now a Pantry Pal user!
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleRedirect} autoFocus color="primary">
            Login here
          </Button>
        </DialogActions>
      </Dialog>

    </Paper>
  )
}

export default Congrats
