import React, {useState} from 'react';
import IconButton from '@material-ui/core/IconButton';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import MoreVertIcon from '@material-ui/icons/MoreVert';

const ITEM_HEIGHT = 48;

//need to tie in shop list actions here

const ItemActions = (props) => {
  const [anchorEl, setAnchorEl] = useState(null);
  const [id, setId] = useState(props.item)

  const options = [
    'Update',
    'Remove'
  ];

  const open = Boolean(anchorEl);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleDelete = (id) => {
    //need to add actions here 
    console.log('you are trying to delete this', id)
  }

  const handleUpdate = () => {
    //need to add actions here 
    console.log(' you are trying to update this')
  }


  return (
    <div>
      <IconButton
        aria-label="more"

        aria-haspopup="true"
        onClick={handleClick}
      >
       <MoreVertIcon/>
      </IconButton>
      <Menu

        anchorEl={anchorEl}
        keepMounted
        open={open}
        onClose={handleClose}
        PaperProps={{
          style: {
            maxHeight: ITEM_HEIGHT * 4.5,
            width: '20ch',
          },
        }}
      >
        {options.map((option) => (
          <MenuItem key={option.value} value={id} selected={option === 'Update'} selected={option === 'Delete'} >
            <div onClick={handleUpdate} select={option==='Update'}></div>
          </MenuItem>
        ))}
      </Menu>
    </div>
  );
}

export default ItemActions
