import {LOGIN} from '../actions/types'

const startState = {
    user:[],
    validation:false
}

export default function(state = startState, action){

    switch(action.type){
        case LOGIN:
        const newState = {
            user: [action.payload],
            validation: true
        }
        return state
        default: return state
    }

}
